document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".parallax-section");if(e.length>0&&window.innerWidth>767){const t=new IntersectionObserver(e=>{e.forEach(e=>{if(e.isIntersecting){const t=e.target;let n=0;window.addEventListener("scroll",()=>{const e=t.getBoundingClientRect().top,l=window.pageYOffset,s=e+l-window.innerHeight/2,a=l-s;n=a*-.3,t.style.backgroundPositionY=n+"px"})}})},{threshold:.1});e.forEach(e=>{t.observe(e)})}}); 